import React, { useState } from 'react';
import { Card, CardImg, CardImgOverlay, CardTitle, Button } from 'reactstrap';

function ListOrder({ example }) {
    return (
        <Card>
            <CardImg src={example.image} alt={example.name} width="5px" height="50%"></CardImg>
            <CardImgOverlay>
                <CardTitle>{example.name}</CardTitle>
            </CardImgOverlay>
        </Card>
    );
}

function Order(props) {
    // const [STATE ITEM, SET STATE FUNCTION] = USE STATE(INITIAL STATE VALUE)
    const [foodItem, setFoodItem] = useState(1);

    console.log('our state: ', foodItem);

    const [checkOn, setCheckOn] = useState(true);
    console.log('Checking Checks: ', checkOn);

    const item = props.examples.map(example => {
        return (
            <div key={example.id} className="col-md-5 m-1" >
                <ListOrder example={example} />
                <button class="decrement counter-btn">-</button>
                <input type="number" placeholder="Quantity" readonly="" class="quantity" value={this.foodItem} />
                <button class="increment counter-btn" onClick={() => setFoodItem(foodItem + 1)} >+</button>
                <Button>order now</Button>
            </div>
        );
    });

    return (
        <div className="container">
            <div className="row">
                {item}
            </div>
        </div>
    )
}

export default Order;
