import React, { Component } from 'react';
import { Grid } from '@material-ui/core';

import './Home.css';
export default class Home extends Component {

    render() {
        typing_effect(['Hello World', 'My name is Harry', 'I am a programmer', 'This is a JS+CSS example'],
            ['#FFFFFF', 'orange', 'blue', 'yellow']);

        function typing_effect(words, colors) {

            var cursor = document.getElementById('cursor'); //cursor
            var text = document.getElementById('text') //text

            var blink = true;
            var wait = false;
            var letters_count = 1;
            var temp = 1;

            // text.style.color = colors[0]
            window.setInterval(function () { //wait between words when it starts writting
                if (letters_count === 0 && wait === false) {
                    wait = true;

                    text.innerHTML = '' // leave a blank
                    window.setTimeout(function () {
                        var usedColor = colors.splice(0, 1)[0] //remove first element and get it as str
                        colors.push(usedColor);
                        var usedWord = words.splice(0, 1)[0]
                        words.push(usedWord);
                        temp = 1;
                        text.style.color = colors[0]
                        letters_count += temp;
                        wait = false;
                    }, 1000)
                } else if (letters_count === words[0].length + 1 && wait === false) {
                    wait = true;
                    window.setTimeout(function () { //wait a bit until words finished and start deleting
                        temp = -1;
                        letters_count += temp;
                        wait = false;
                    }, 1000)
                } else if (wait === false) { //write words                    
                    text.innerHTML = words[0].substr(0, letters_count)
                    letters_count += temp;
                }
            }, 120)
            window.setInterval(function () {
                if (blink) {
                    cursor.style.opacity = "0";
                    blink = false;
                } else {
                    cursor.style.opacity = "1";
                    blink = true;
                }
            }, 400)
        }
        return (
            <div>
                {/* <div className="col-md-10">
                    <h1>
                        <a className="typewrite text-over-img " data-period="2000" data-type='[ "Welcome to Havre De Grace", "Discover your Maryland", "Welcome to Marias Pizza", "The best in Town." ]'>
                        </a>
                    </h1>
                    <img className="img" src="/assets/images/hdg.png" width="100%" height="600" />



                </div> */}
                <div className="container">
                    <img src="https://i.ibb.co/6bTCMTH/jumbotron1.jpg" alt="Hero Image" />
                    <div className='console-container'>
                        <span id='text'></span>
                        <div className='console-underscore' id='cursor'>|</div>
                    </div>
                </div>





                <hr></hr>
                <div className="d-grid gap-2 d-md-flex justify-content-md-center social" >
                    <a href="http://instagram.com/"><i class="btn btn-dark btn-lg bi bi-facebook col"></i></a>
                    <a href="http://www.facebook.com/"><i class="btn btn-dark btn-lg bi bi-instagram col btn-lg"></i></a>
                    <a role="button" href="tel:+14109420001"><i class="btn btn-dark btn-lg bi bi-telephone-fill col btn-lg" /></a>
                    <a role="button" href="mailto:notreal@notreal.co"><i class="btn btn-dark btn-lg bi bi-envelope-fill col btn-lg"></i></a>
                </div>
                {/* <div id="carouselExampleControls" class="carousel slide" data-ride="carousel">
                    <div class="carousel-inner">
                        <div class="carousel-item active">
                            <img class="d-block w-100" src="/assets/images/boat.png" alt="First slide" />
                        </div>
                        <div class="carousel-item">
                            <img class="d-block w-100" src="/assets/images/cap.png" alt="Second slide" />
                        </div>
                        <div class="carousel-item">
                            <img class="d-block w-100" src="/assets/images/shrimp.png" alt="Third slide" />
                        </div>
                    </div>
                    <a class="carousel-control-prev" href="#carouselExampleControls" role="button" data-slide="prev">
                        <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                        <span class="sr-only">Previous</span>
                    </a>
                    <a class="carousel-control-next" href="#carouselExampleControls" role="button" data-slide="next">
                        <span class="carousel-control-next-icon" aria-hidden="true"></span>
                        <span class="sr-only">Next</span>
                    </a>
                </div> */}

                {/* 
                <div class="MuiGrid-root MuiGrid-container MuiGrid-spacing-xs-3">
                    <div class="MuiGrid-root MuiGrid-item MuiGrid-grid-xs-12 MuiGrid-grid-sm-6 MuiGrid-grid-lg-3" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                        <i class="bi bi-person-fill"></i>
                        <h6 class="fact-heading">Create an Account.</h6>
                        <p class="fact-text">If you are an existing customer.</p>
                    </div>
                    <div class="MuiGrid-root MuiGrid-item MuiGrid-grid-xs-12 MuiGrid-grid-sm-6 MuiGrid-grid-lg-3" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                        <i class="bi bi-plus-lg"></i>
                        <h6 class="fact-heading">Sign Up for Maria's Rewards.</h6>
                        <p class="fact-text">If you are an existing customer.</p>
                    </div>
                    <div class="MuiGrid-root MuiGrid-item MuiGrid-grid-xs-12 MuiGrid-grid-sm-6 MuiGrid-grid-lg-3" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                        <i class="bi bi-emoji-heart-eyes"></i>
                        <h6 class="fact-heading">Place you order.</h6>
                        <p class="fact-text">If you are an existing customer.</p>
                    </div>

                </div> */}
                <div class="wrapper reward">

                    <h1>Maria's Rewards Program .</h1>

                </div>

                <div className="Rewards">
                    {/* simple design implementation */}
                    <div className="d-grid gap-2 d-md-flex justify-content-md-center m-3 p-3">
                        <div>
                            <a href="www.selectmariaspizza.com"><i class="bi bi-person-fill" ></i></a>
                            <h6 class="fact-heading">Create an Account.</h6>
                            <p>If you are a New Customer.</p>
                        </div>
                        <div>
                            <a href="www.selectmariaspizza.com"><i class="bi bi-plus-lg"></i></a>
                            <h6 class="fact-heading">Sign Up for Maria's Rewards.</h6>
                            <p>If you are an existing customer.</p>
                        </div>
                        <div>
                            <a href="www.selectmariaspizza.com"> <i class="bi bi-emoji-heart-eyes"></i></a>
                            <h6 class="fact-heading">Place you order.</h6>
                            <p>If you are an existing customer.</p>
                        </div>

                    </div>
                </div>

                <div class="wrapper reward">

                    <h1>Place your Order .</h1>

                </div>


                <div className="orders">
                    {/* simple design implementation */}
                    <div className="d-grid gap-2 d-md-flex justify-content-md-center m-3 p-3">
                        <div>
                            <a href="www.selectmariaspizza.com"><i class="bi bi-telephone-fill"></i></a>
                            <h6 class="fact-heading">Call</h6>
                            <p>Call us in store to place your order</p>
                        </div>
                        <div>
                            <a href="www.selectmariaspizza.com"><i class="bi bi-bag-check-fill"></i>
                            </a>
                            <h6 class="fact-heading">Pick-Up</h6>
                            <p>Why wait in line ?</p>
                        </div>
                        <div>
                            <a href="www.selectmariaspizza.com"> <i class="bi bi-truck"></i>
                            </a>
                            <h6 class="fact-heading">Delivery</h6>
                            <p>Why even drive? Let us deliver.</p>
                        </div>

                    </div>
                </div>
            </div >



        );
    }
}
