import React, { Component } from 'react';

export default class ShoppingCart extends Component {
    constructor(props) {
        super(props);

    }
    // --> console log the props to check what is inside it

    numberCart() {

        console.log("props : ", this.props);
        // check the states of the props
        console.log("states :", this.props.states)
        // the states is an array of objects with quantity and imageName as the key now check the quantity from each of the states
        console.log("states->Quantity: ", this.props.states.quantity);
    }

    render() {

        this.numberCart();

        return (
            <div>
                <p>this is your Order: {this.props.states.map((element) => <p> You have ordered {element.quantity} {element.imageName}</p>)}</p>
            </div>
        );
    }


}
