import React, { Component } from 'react'
import Navtab from '../components/Navbar'
import Footer from '../components/footer'
import { EXAMPLES } from '../testData/example'
import { Route, Switch, Redirect } from 'react-router'
import Order from './Order'
class Home extends Component {
    constructor(props) {
        super(props)
        this.state = {
            examples: EXAMPLES,
            shoppingCart: []
        }
    }

    // TODO: Write the function that can add an item into our shopping cart

    render() {
        const homepage = () => {
            return (
                <Home />
            );
        }

        const order = () => {
            return (
                <Order examples={this.state.examples} shoppingCart={this.state.shoppingCart} />
            );
        }

        return (
            <div>
                <Navtab />
                <Switch>
                    <Route path='/home' component={homepage} />
                    <Route exact path='/GiftCards' component={order} />
                    <Redirect to='/home' />
                </Switch>
                <Footer />
            </div>
        )
    }
}
export default Home;
