import React, { Component } from 'react'
import Navbar from '../components/Navbar';
import Button from '../components/Button';


export default class FoodOrder extends Component {

  constructor(props) {
    super(props)

    this.state = {

      method: null
    }
  }

  renderInput() {
    if (this.state.method === null) {
      return (

        <p>Select your preferred mode of order:</p>
      )

    }
    if (this.state.method == "pickup") {
      return (

        <p>Customer chose to pick up.</p>

      )

    }
    return <p>Customer chose to deliver.</p>

  }

  setMethod = (e) => {

    this.setState({ method: e.target.value })
    console.log("On Change", e.target)

  }

  render() {
    // if the customer chooses to pick up the oder, we will ask for a name. then move to taking orders
    // but if the customer chooses to get the food delivered, we will ask for their address and then move to taking orders.

    // if pickup is true
    //       render a form for their name (Restaurant's Address)
    // else
    //       render a form for their address



    return (
      <div>
        <Navbar />
        <h1 className="heading">Maria's Pizza</h1>
        <Button />
        <div class="form-check">
          <input class="form-check-input" type="radio" checked={this.state.method == "pickup"} value="pickup" onChange={this.setMethod} />
          <label class="form-check-label" for="flexRadioDefault1">
            Pick up
  </label>
        </div>
        <div class="form-check">
          <input class="form-check-input" type="radio" checked={this.state.method == "delivery"} value="delivery" onChange={this.setMethod} />
          <label class="form-check-label" for="flexRadioDefault2">
            Delivery
  </label>
        </div>

        {this.renderInput()}
      </div>
    )
  }
}
