import React, { Component } from 'react';

export default class ShoppingCart extends Component {
    constructor(props) {
        super(props);

    }

    // --> loop through the items in states
    // --> write a console.log test for each of the items in the array
    // --> log the quantity value to make sure we're able to access it

    // --> create a global variable to collect the quantity of each array


    numberCart() {
        var sum = 0;
        console.log(this.props.states);
        this.props.states.forEach(cartItem => {
            sum = sum + cartItem.quantity;
        });

        return sum;
    }

    render() {

        console.log(this.numberCart());

        return (
            <div>
                <p>this is your Order: {this.props.states.map((element) => <p> You have ordered {element.quantity} {element.imageName}</p>)}</p>
            </div>
        );
    }


}
