import { Button } from 'reactstrap';
import FoodItem from '../components/FoodItem';
import React, { useState } from 'react';


function Order(props) {


    const items = {
        quantity: 0,
        imageName: ''
    }


    const item = props.examples.map(example => {
        return (
            <div key={example.id} className="col-md-5 m-1" >
                <FoodItem example={example} updateShoppingCart={props.updateShoppingCart} />
            </div>
        );
    });
    return (
        <div>
            <div className="container">
                <div className="row">
                    {item}
                </div>

            </div>
            <Button onClick={() => props.updateShoppingCart(items)}>Clear Your Cart</Button>
        </div>
    );
}

export default Order;
