import React, { Component } from 'react';
import './ShoppingCart.css';
export default class Contact extends Component {
    render() {
        var now = new Date();
        var weekday = new Array(7);
        weekday[0] = "Sunday";
        weekday[1] = "Monday";
        weekday[2] = "Tuesday";
        weekday[3] = "Wednesday";
        weekday[4] = "Thursday";
        weekday[5] = "Friday";
        weekday[6] = "Saturday";

        var checkTime = function () {
            var today = weekday[now.getDay()];
            var timeDiv = document.getElementById('timeDiv');
            var dayOfWeek = now.getDay();
            var hour = now.getHours();
            var minutes = now.getMinutes();

            //add AM or PM
            var suffix = hour >= 12 ? "PM" : "AM";

            // add 0 to one digit minutes
            if (minutes < 10) {
                minutes = "0" + minutes
            };

            if ((dayOfWeek == 0 || dayOfWeek == 6) && hour >= 13 && hour <= 23) {
                hour = ((hour + 11) % 12 + 1); //i.e. show 1:15 instead of 13:15
                timeDiv.innerHTML = 'it\'s ' + today + ' ' + hour + ':' + minutes + suffix + ' - we\'re open!';
                timeDiv.className = 'open';
            }

            else if ((dayOfWeek == 3 || dayOfWeek == 4 || dayOfWeek == 5) && hour >= 16 && hour <= 23) {
                hour = ((hour + 11) % 12 + 1);
                timeDiv.innerHTML = 'it\'s ' + today + ' ' + hour + ':' + minutes + suffix + ' - we\'re open!';
                timeDiv.className = 'open';
            }

            else {
                if (hour == 0 || hour > 12) {
                    hour = ((hour + 11) % 12 + 1); //i.e. show 1:15 instead of 13:15
                }
                timeDiv.innerHTML = 'It\'s ' + today + ' ' + hour + ':' + minutes + suffix + ' - we\'re closed!';
                timeDiv.className = 'closed';
            }
        };

        var currentDay = weekday[now.getDay()];
        var currentDayID = "#" + currentDay; //gets todays weekday and turns it into id
        $(document).ready(function () {
            $(currentDayID).toggleClass("today");
        });
        //hightlights today in the view hours modal popup

        setInterval(checkTime, 1000);
        checkTime();
        return (
            <div className="text-center">
                <h1>Contact Us</h1>
                <hr></hr>
                <p>Better yet, see us in person!</p>
                <p>"We love our customers, <br />so feel free to visit during normal business hours."</p>
                <p>Maria's Pizza<br />
                    866 Otsego St<br />
                    HAVRE DE GRACE, MD 21078<br />
                </p>
                <p> 410-942-0001</p>
                <p>Hours of Operation</p>


                <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3076.3219916092744!2d-76.1027076847766!3d39.552346679474894!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x89c7c74cd36c54ab%3A0x3e4451ddaee7fb24!2sMaria&#39;s%20Pizza!5e0!3m2!1sen!2sus!4v1631089738157!5m2!1sen!2sus"
                    width="100%" height="450" allowfullscreen="" loading="lazy"></iframe>
            </div>


        );
    }
}
