import React, { Component } from 'react';
import { Nav, Navbar, NavbarBrand, NavbarToggler, Collapse, NavItem } from 'reactstrap';
import { NavLink } from 'react-router-dom';
import Button from './Button';
import './Navbar.css';
class Navtab extends Component {
    constructor(props) {
        super(props);
        this.toggleNav = this.toggleNav.bind(this);
        this.state = {
            isNavOpen: false
        };
    }

    // negative items check
    // reset the state/ clear the shopping cart
    // react-toastify
    toggleNav() {
        this.setState({
            isNavOpen: !this.state.isNavOpen
        });
    }
    render() {
        return (
            <React.Fragment>
                <div>
                    <nav class="navbar navbar-expand-lg navbar-light bg-light">
                        <a class="navbar-brand" href="#"></a>
                        <img src="/assets/images/marialogo.png" height="100" width="100" alt="Maria's Pizza Logo" />
                        <div class="collapse navbar-collapse" id="navbarNavDropdown">
                            <ul class="navbar-nav">
                                <li class="nav-item active">
                                    <a class="nav-link" href="/home"> <i class="bi bi-house-fill" /> Home <span class="sr-only">(current)</span></a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" href="/souvenirs"> <i class="bi bi-gift-fill" /> souvenirs</a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" href="/Cart"> <i class="bi bi-cart"></i>  Cart {this.props.cartItems}</a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" href="/Contact">   <i class="bi bi-geo-alt"></i> Contact Us</a>
                                </li>

                            </ul>
                        </div>
                    </nav>
                </div>

            </React.Fragment>
        );
    }
}
export default Navtab;
