import React, { useState } from 'react';
import { Card, CardImg, CardImgOverlay, CardTitle, Button } from 'reactstrap';

export default function FoodItem(props) {
    // const [STATE ITEM, SET STATE FUNCTION] = USE STATE(INITIAL STATE VALUE)
    // TODO: rename to quantity
    const [foodItem, setFoodItem] = useState(1);

    // TODO: when we click the order now button...
    // call a function that takes the food item data, and the quantity selected
    // and adds it to the state in the `home.js` file
    function updateCart(foodItem, props) {
        props.setState = {
            shoppingCart: [{ "quantity": "5" }, { "imageName": "King" }]
        }
        console.log("Cart Update: ", props.state.shoppingCart.quantity);

    }
    return (
        <div>
            <Card>
                <CardImg src={props.example.image} alt={props.example.name} width="5px" height="50%"></CardImg>
                <CardImgOverlay>
                    <CardTitle>{props.example.name}</CardTitle>
                </CardImgOverlay>
            </Card>

            <button class="decrement counter-btn" onClick={() => setFoodItem(foodItem - 1)}>-</button>
            <input type="number" placeholder="Quantity" readonly="" class="quantity" value={foodItem} />
            <button class="increment counter-btn" onClick={() => setFoodItem(foodItem + 1)} >+</button>
            <Button onClick={() => updateCart(foodItem, props)}>order now</Button>
        </div>
    )
}
