import React, { Component } from 'react'
import Navbar from './Navbar';
import Button from './Button';
import './App.css';

export default class App extends Component {

  constructor(props) {
    super(props)

    this.state = {
       isPickup: false
    }
  }

  renderInput(){
    if(this.state.isPickup){
      return(

        <p>Customer chose to pick up.</p>

      )

    }
   return <p>Customer chose to deliver.</p>
  }

  render() {
    // if the customer chooses to pick up the oder, we will ask for a name. then move to taking orders
    // but if the customer chooses to get the food delivered, we will ask for their address and then move to taking orders.

   // if pickup is true
   //       render a form for their name (Restaurant's Address)
   // else
   //       render a form for their address



    return (
      <div>
         <Navbar/>
        <h1 className="heading">Maria's Pizza</h1>
        <Button/>
        <div class="form-check">
  <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1"/>
  <label class="form-check-label" for="flexRadioDefault1">
    Pick up
  </label>
</div>
<div class="form-check">
  <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" checked/>
  <label class="form-check-label" for="flexRadioDefault2">
    Delivery
  </label>
</div>
        {this.state.isPickup}
        {this.renderInput()}
      </div>
    )
  }
}
